-- tablas estadisticasdelete SAEPRDwhere Periodo<='201812'delete SAECLIwhere Periodo<='201812'delete SAECOMwhere Periodo<='201812'delete SAEVENwhere Periodo<='201812'delete SAEVTAwhere Periodo<='201812'-- cuentas por cobrar y pagardelete SAACXCwhere FechaE <='31-12-2018'delete SAACXPwhere FechaE <='31-12-2018'delete SAFACTwhere FechaE <='31-12-2018'delete SAITEMFACwhere FechaE <='31-12-2018'delete SACOMPwhere FechaE <='31-12-2018'delete SAITEMCOMwhere FechaE <='31-12-2018'delete SAOPEIwhere FechaE <='31-12-2018'delete SAITEMOPIwhere FechaE <='31-12-2018'delete SAINITI  --15segwhere Periodo<='201812'select * from SATAXVTA tf left join SAFACT f on f.NumeroD = tf.NumeroD and f.TipoFac = tf.TipoFacwhere f.NumeroD is not null-- borrar las tablas relacionadasdelete tf from SATAXVTA tf left join SAFACT f on f.NumeroD = tf.NumeroD and f.TipoFac = tf.TipoFacwhere f.NumeroD is nulldelete tf from SATAXITF tf left join SAITEMFAC f on f.NumeroD = tf.NumeroD and f.TipoFac = tf.TipoFac and f.NroLinea=tf.NroLineawhere f.NumeroD is nulldelete tc from SATAXCXC tc left join SAACXC c on c.NroUnico = TC.NroPpal where c.NumeroD is  null